interface EmojiPickerProps {
    selectedEmoji: string;
    onSelect: (emoji: string) => void;
    onClose?: () => void;
}

export default function EmojiPicker({ selectedEmoji, onSelect, onClose }: EmojiPickerProps) {
    const [activeCategory, setActiveCategory] = useState('smileys');
    const [searchQuery, setSearchQuery] = useState('');

    // Flatten all emojis for search
    const allEmojis = useMemo(() => {
        const emojis: string[] = [];
        Object.values(EMOJI_CATEGORIES).forEach(cat => {
            emojis.push(...cat.emojis);
        });
        return emojis;
    }, []);

    // Filter emojis based on search
    const displayedEmojis = useMemo(() => {
        if (searchQuery) {
            // Simple search - in a real app you'd use emoji names/keywords
            return allEmojis.slice(0, 100); // Return first 100 for performance
        }
        return EMOJI_CATEGORIES[activeCategory]?.emojis || [];
    }, [searchQuery, activeCategory, allEmojis]);

    const categoryIcons: Record<string, string> = {
        smileys: 'ğŸ˜€',
        love: 'â¤ï¸',
        gestures: 'ğŸ‘‹',
        animals: 'ğŸ¶',
        nature: 'ğŸŒ¿',
        food: 'ğŸ',
        activities: 'âš½',
        objects: 'ğŸ’¡',
        symbols: 'â¤ï¸',
        flags: 'ğŸ',
    };

    return (
        <div className="bg-[var(--card)] border border-[var(--border)] rounded-2xl p-4 w-80 max-h-96 overflow-hidden shadow-xl">
            {/* Search */}
            <input
                type="text"
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                placeholder="Search emojis..."
                className="input w-full mb-3 text-sm"
            />

            {/* Category tabs */}
            {!searchQuery && (
                <div className="flex gap-1 mb-3 overflow-x-auto pb-2">
                    {Object.keys(EMOJI_CATEGORIES).map((key) => (
                        <button
                            key={key}
                            onClick={() => setActiveCategory(key)}
                            className={`p-2 rounded-lg text-lg transition-all ${activeCategory === key
                                ? 'bg-[var(--primary)] scale-110'
                                : 'hover:bg-[var(--border)]'
                                }`}
                            title={EMOJI_CATEGORIES[key].name}
                        >
                            {categoryIcons[key]}
                        </button>
                    ))}
                </div>
            )}

            {/* Emoji grid */}
            <div className="h-48 overflow-y-auto">
                <div className="grid grid-cols-8 gap-1">
                    <AnimatePresence mode="popLayout">
                        {displayedEmojis.map((emoji, index) => (
                            <motion.button
                                key={`${emoji}-${index}`}
                                initial={{ opacity: 0, scale: 0.8 }}
                                animate={{ opacity: 1, scale: 1 }}
                                exit={{ opacity: 0, scale: 0.8 }}
                                transition={{ delay: index * 0.005 }}
                                onClick={() => {
                                    onSelect(emoji);
                                    onClose?.();
                                }}
                                className={`p-1.5 text-xl rounded-lg hover:bg-[var(--border)] transition-all ${selectedEmoji === emoji ? 'bg-[var(--primary)]/20 ring-2 ring-[var(--primary)]' : ''
                                    }`}
                                whileHover={{ scale: 1.2 }}
                                whileTap={{ scale: 0.9 }}
                            >
                                {emoji}
                            </motion.button>
                        ))}
                    </AnimatePresence>
                </div>
            </div>

            {/* Selected emoji preview */}
            <div className="mt-3 pt-3 border-t border-[var(--border)] flex items-center justify-between">
                <span className="text-sm text-[var(--muted)]">Selected:</span>
                <span className="text-3xl">{selectedEmoji}</span>
            </div>
        </div>
    );
}
